import React, { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import './ProjectDetail.css';
import BalanceText from 'react-balance-text';
import ProjectDetailSlider from './components/ProjectDetailSlider'
import OtherWorkCards from './components/OtherWorkCards'

function ProjectDetail(props) {
    // const projectSlug = props.match.params.project
    const {
        params: { project },
    } = props.match;
    const history = useHistory();
    const [projectData, setProjectData] = useState(null)
    const [isSingleMedia, setIsSingleMedia] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const childRef = useRef();

    useEffect(() => {
        setIsLoading(true);
        // fetchProject();
        findProject();
        scrollToTop();
        setIsLoading(false);
    }, [project])

    function scrollToTop() {
        if (window.pageYOffset){
            window.scrollTo({
                top: 0,
                left: 0,
                behavior: 'smooth'
              });
        }
    }

    function findProject() {
        var foundProject = false
        while (foundProject === false) {
            props.projects.forEach(function(i) {
                if (i.meta.slug === project) {
                    setProjectData(i)
                    foundProject = true;
                }
            });
        }
    }

    // function fetchProject() {
    //     let id = []
    //     fetch(`https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?slug=${project}`)
    //     .then(response => response.json())
    //     .then(json => {
    //         id = json.items[0].id
    //         fetch(`https://sarah-ryder-backend.herokuapp.com/api/v2/pages/${id}/`)
    //             .then(response => response.json())
    //             .then(json => setProjectData(json))
    //     })
    //     .catch(function() {
    //         console.log("Error: Failed to fetch data from API")
    //         history.push('/')
    //     })
    // }

    function handleLeftClick(e) {
        try {
            childRef.current.handleLeftClick(e);
        }
        catch(err) {
            console.log(err)
        }
      }

    function handleRightClick(e) {
        try {
            childRef.current.handleRightClick(e)
        }
        catch(err) {
            console.log(err)
        }
      }



      if (!isLoading) {
        return (
            <div className="project-detail">
                <div className="project-detail-left" onClick={handleLeftClick}/>
                <div className="project-detail-right" onClick={handleRightClick}/>
                <div className="project-detail-flex-container">
                    <div className="project-detail-image-and-description-container">
                        <div className="project-slider-flex-container">
                            <div className="project-slider-container">
                                    <div>
                                        <ProjectDetailSlider mediaGallery={projectData.media_gallery} images={props.images} ref={childRef}/>
                                    </div>
                            </div>
                        </div>
                        <div className="project-info">
                            <BalanceText>
                                {projectData.full_description}
                            </BalanceText>
                        </div>
                    </div>
                    <div className="project-credits">
                        <span dangerouslySetInnerHTML={{__html:projectData.project_credits}}/>
                    </div>
                </div>
                <div className="other-work-container">
                    <div>
                        <OtherWorkCards projects={props.projects} currentProject={project} isLoading={isLoading} setIsLoading={setIsLoading}/>
                    </div>
                </div>
                <div className="copyright-container">
                    <p>&#169; {new Date().getFullYear()}</p>
                </div>
            </div>
        )
    } else {
        return (<div/>)
    }

}

  export default ProjectDetail;
