import React, { useState, useEffect } from 'react';
import { Route, Switch, NavLink, Redirect } from 'react-router-dom';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import ProjectsSlider from './components/ProjectsSlider/ProjectsSlider';
import Info from './components/Info/Info';
import ProjectDetail from './components/ProjectDetail/ProjectDetail';
import './Main.css';

function App() {
  const [projects, setProjects] = useState(null)
  const [info, setInfo] = useState(null)
  const [images, setImages] = useState(null)

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    loadImages();
  }, [images]);

  function fetchData() {
    fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?type=portfolio.Project&fields=client,card_image,card_description,media_gallery')
      .then(response => response.json())
      .then(json => setProjects(json.items))

    fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?type=home.HomePage&fields=bio,clients,email,linkedin,instagram')
      .then(response => response.json())
      .then(json => setInfo(json.items[0]))

    fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/images/?limit=1000')
      .then(response => response.json())
      .then(json => setImages(json))        
  }

  function loadImages() {
    imagesTemp = []
    images.map(image => {
      console.log(image)
    })
  }

  console.log(images)

  if (projects && info && images) {
    return (
      <div className="app-container">
        <div className="navbar">
          <NavLink to="/"><div id="logo"></div></NavLink>
          <h1><NavLink to="/info">Info</NavLink></h1>
        </div>
        <div className="content">
          <Route render={({location}) => (
            <TransitionGroup id="transition-group">
              <CSSTransition
                key={location.key}
                timeout={500}
                classNames="page fade"
              >
                <div id="routes-container">
                  <Switch location={location}>
                    <Route 
                      exact path="/"
                      render={(props) => (
                        <ProjectsSlider {...props} projects={projects} />
                      )}
                    />
                    <Route 
                    exact path="/info" 
                    render={(props) => (
                      <Info {...props} info={info} />
                    )}
                    />
                    <Route
                      path="/:project"
                      render={(props) => (
                        <ProjectDetail {...props} projects={projects} images={images} />
                      )}
                    />
                    <Redirect to="/" />
                  </Switch>
                </div>
              </CSSTransition>
            </TransitionGroup>
          )} />
        </div>
      </div>
    );
  } else {
    return <div>LOADING</div>
  }
}

export default App;
