import React, { useState, useEffect } from 'react';
import './Info.css';

function Info() {
    const [sarahInfo, setSarahInfo] = useState([])

    useEffect(() => {
        fetchSarahInfo();
    }, []);

    function fetchSarahInfo() {
        fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?type=home.HomePage&fields=bio,clients,email,linkedin,instagram')
            .then(response => response.json())
            .then(json => setSarahInfo(json.items[0]))
    }

    return(
        <div className="info-page-flex-container">
            <div className="info-container">
                <div>
                <div id="bio-container">
                    <div dangerouslySetInnerHTML={{__html:sarahInfo.bio}} id="bio"/>
                </div>
                <div id="info-lists">
                    <div id="info-lists-flex-container">
                        <div id="selected-clients">
                            <p>Selected Clients</p>
                            <div dangerouslySetInnerHTML={{__html:sarahInfo.clients}} />
                        </div>
                        <div id="contact">
                            <p>Contact</p>
                            <ul>
                                <li><a href={"mailto: " + sarahInfo.email}>email</a></li> 
                                <li><a href={sarahInfo.linkedin}>linkedin</a></li>
                                <li><a href={sarahInfo.instagram}>instagram</a></li>
                            </ul>
                        </div>
                    </div>
                </div>
                </div>
            </div>
            <div className="copyright-container" id="info-copyright">
                    <p>&#169; {new Date().getFullYear()}</p>
            </div>
        </div>
    )
}

export default Info;
