import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './OtherWorkCards.css';

function OtherWorkCards(props) {
    // const [currentProjectID, setCurrentProjectID] = useState(null)
    const [pages, setPages] = useState(null)
    const [cardsIsLoading, setCardsIsLoading] = useState(true)

    useEffect(() => {
        setCardsIsLoading(true)
        // fetchCurrentProjectID();
        fetchPages();
        setCardsIsLoading(false)
      }, [props]);

    // function fetchCurrentProjectID() {
    //     fetch(`https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?slug=${props.currentProject}`)
    //     .then(response => response.json())
    //     .then(json => setCurrentProjectID(json.items[0].id))
    //     .catch(function() {
    //         console.log("Error: Failed to fetch data from API")
    //     })
    // }

    function fetchPages() {
        fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?type=portfolio.Project&fields=client,card_image,card_description')
        .then(response => response.json())
        .then(json => setPages(json.items))
    }

    console.log(pages)

    if (pages) {
        return(
            <div className="card-holder">
                {pages.map((page, index) => {
                    if (page.meta.slug !== props.currentProject) {
                        return (
                            <Link to={"/" + page.meta.slug} key={index} className="project-card" >
                                <div className="card-image">
                                    <img
                                        src={'https://sarah-ryder-backend.s3-us-west-2.amazonaws.com/original_images/' + page.card_image.title}
                                        alt={page.card_description}
                                    />
                                </div>
                                <div className="card-text">
                                    <p className="detail-page-card-client">{page.client}</p>
                                    <p className="detail-page-card-description">{page.card_description}</p>
                                </div>
                            </Link>
                        )
                    }
                }
                )}
            </div>
        )
    } else return (
        <div/>
    )

}

export default OtherWorkCards;
