import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/themes/splide-default.min.css';
import './ProjectsSlider.css';

function ProjectsSlider() {
  const [pages, setPages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const ref = useRef();

  const sliderOptions = {
      rewind: true,
      perMove: 0,
      gap: '3vw',
      type: 'loop',
      autoWidth: true,
      pagination: false,
      arrows: false,
      focus: 'center',
      trimspace: false,
  }

  useEffect(() => {
    setIsLoading(true);
    fetchPages();
    // handleReset();
    setIsLoading(false);
  }, []);

  function fetchPages() {
    fetch('https://sarah-ryder-backend.herokuapp.com/api/v2/pages/?type=portfolio.Project&fields=client,card_image,card_description')
      .then(response => response.json())
      .then(json => setPages(json.items))
  }

  function handleLeftClick(e) {
    try {
      ref.current.splide.go('<');
    }
    catch(err) {
      console.log(err)
    }
  }

  function handleRightClick(e) {
    try {
      ref.current.splide.go('>');
    }
    catch(err) {
      console.log(err)
    }
  }

  function handleReset(e) {
    try {
      ref.current.splide.go('+{2}');
    }
    catch(err) {
      console.log(err)
    }
  }

if (!isLoading) {
  return (
    <div id="projects-slider-flex-container">
      <div id="buttons-top-container">
        <div id="slider-button-top-left" onClick={handleLeftClick}/>
        <div id="slider-button-top-right" onClick={handleRightClick}/>
      </div>
      <div className="projects-slider-container">
        <Splide
          options={ sliderOptions }
          ref={ref}
          >
          {pages.map((page, index) => (
            <SplideSlide key={index}>
              <Link to={"/" + page.meta.slug} onClick={handleReset()} className="nav-link">
                <div className="projects-slider-card-container">
                  <div className="projects-slider-card-image-container">
                    <img src={'https://sarah-ryder-backend.s3-us-west-2.amazonaws.com/original_images/' + page.card_image.title} alt={page.card_description}/>
                  </div>
                  <div className="projects-slider-card-text-container">
                    <p className="projects-slider-client-name">{page.client}</p>
                    <p className="projects-slider-card-description">{page.card_description}</p>
                  </div>
                </div>
              </Link>
            </SplideSlide>
          ))}
        </Splide>
      </div>
      <div id="buttons-bottom-container">
        <div id="slider-button-bottom-left" onClick={handleLeftClick}/>
        <div id="slider-button-bottom-right" onClick={handleRightClick}/>
      </div>
    </div>
  );
} else {
  return <div/>
}

  }

  export default ProjectsSlider;
